import React, { Component } from 'react'
import FacebookLogin from 'react-facebook-login';

export default class Facebook extends Component {
    state = {
        isLoggedIn: false,
        userID: '',
        name: '',
        email: '',
        picture: ''
    }

    componentClicked = () => {
        console.log("clicked");
    }

    componentFailure = () => {
        console.log("Failed");
    }

    responseFacebook = response => {
        console.log(response);
        this.setState({ 
            isLoggedIn: true,
            userID: response.userID,
            name: response.name,
            email: response.email,
            picture: response.picture.data.url,
         })
    }

    render() {
        let fbContent;
        let prompt;
        if(this.state.isLoggedIn) {
            prompt = (<h2>Welcome to SocialScene!</h2>);
            fbContent = (
                <div style={{color: 'white'}}>
                    <br/>
                    <img className="profPic" src={this.state.picture} alt={this.state.name} />
                    <h2>{this.state.name}</h2>
                    email: {this.state.email} <br/>
                    userID: {this.state.userID}
                </div>
            );
        } else {
            prompt = (
            <div>
                <p>
                    To get started, <br/>Please Login With Facebook.
                    <br/><br/>
                </p>
            </div>
            )
            fbContent = 
            (<FacebookLogin
            appId="454141895144280"
            autoLoad={true}
            data-use-continue-as="true"
            fields="name,email,picture"
            scope="public_profile,email"   // user_friends
            onClick={this.componentClicked}
            callback={this.responseFacebook}
            onFailure={this.componentFailure} />);
        }

        return <div>{prompt}{fbContent}</div>;
    }
}
